@app.route("/", methods=["POST"])
def pubsub_push():
    envelope = request.get_json()
    if not envelope:
        return "no Pub/Sub message received", 400
    if not isinstance(envelope, dict) or "message" not in envelope:
        return "invalid Pub/Sub message format", 400
    message = envelope["message"]
    if not isinstance(message, dict):
        return "invalid Pub/Sub message format", 400

    attributes = message.get("attributes", {})
    if not isinstance(attributes, dict):
        return "invalid Pub/Sub message format", 400

    data = message.get("data")
    if data:
        try:
            decoded_data = base64.b64decode(data).decode("utf-8")
            try:
                data_json = json.loads(decoded_data)
            except json.JSONDecodeError:
                data_json = decoded_data
        except Exception as e:
            print(f"Error decoding data: {e}")
            data_json = None

    build_id = attributes.get("buildId")
    if build_id:
        # Process Cloud Build events
        try:
            # Example processing: extract status
            status = data_json.get("status")
            attributes["buildStatus"] = status
        except Exception as e:
            print(f"Error processing Cloud Build event: {e}")


    row_to_insert = attributes
    if data_json:
        row_to_insert.update(data_json)

    try:
        errors = client.insert_rows_json(table_ref, [row_to_insert])
        if errors:
