import os
from datetime import date, datetime

from sanic import Sanic, response, Request
from sanic.exceptions import NotFound, SanicException
from gino import Gino
from sqlalchemy.dialects.postgresql import JSONB

DATABASE_URL = os.environ.get("DATABASE_URL", "postgresql://user:password@host:port/database")

db = Gino()


class ExchangeRates(db.Model):
    __tablename__ = "exchange_rates"

    date = db.Column(db.Date(), primary_key=True)
    rates = db.Column(JSONB())


class ExchangeRateAPI:
    def __init__(self):
        self.app = Sanic("ExchangeRateAPI")
        self.db = db
        self.config = {"DATABASE_URL": DATABASE_URL}
        self._setup_routes()

    def _setup_routes(self):
        self.app.add_route(self.exchange_rates, "/api/latest", methods=["GET"])
        self.app.add_route(self.exchange_rates, "/api/<date:ymd>", methods=["GET"])

    async def exchange_rates(self, request: Request, date: str = None):
        try:
            base = request.args.get("base", "EUR").upper()
            symbols = request.args.get("symbols")
            if symbols:
                symbols = [s.upper() for s in symbols.split(",")]

            if date:
                requested_date = datetime.strptime(date, "%Y-%m-%d").date()
            else:
                requested_date = date.today()

            rates_record = await ExchangeRates.get_or_404(requested_date)
            rates = rates_record.rates

            if base != "EUR":
                if base not in rates:
                    raise NotFound("Base currency not found")
                base_rate = rates[base]
                for currency, rate in rates.items():
                    rates[currency] = rate / base_rate
                rates[base] = 1.0

            if symbols:
                filtered_rates = {
                    currency: rate
                    for currency, rate in rates.items()
                    if currency in symbols or currency == base
                }
                rates = filtered_rates

            return response.json({"base": base, "date": str(requested_date), "rates": rates})

        except (ValueError, NotFound) as e:
            return response.json({"error": str(e)}, status=400 if isinstance(e, ValueError) else 404)
        except SanicException as e:
            return response.json({"error": str(e)}, status=e.status_code)
        except Exception as e:
            return response.json({"error": "Internal Server Error"}, status=500)

    def run(self, host: str = "0.0.0.0", port: int = 8000) -> None:
        self.app.run(host=host, port=port)


if __name__ == "__main__":
    api = ExchangeRateAPI()
    api.run()

