        elif method == "patch":
            return await self.client.patch(url, headers=headers)
        elif method == "put":
            return await self.client.put(url, headers=headers)
        elif method == "delete":
            return await self.client.delete(url, headers=headers)
        else:
            raise ValueError("Invalid HTTP method")

    async def get(self, url: str, headers: Optional[Dict[str, str]] = None) -> response.HTTPResponse:
