from familia.embeddings.topical_word_embeddings import TopicalWordEmbeddingsWrapper

logger = get_logger()

class FamiliaAPI:
    def __init__(self, model_path: str, model_name: str, n_workers: int = None) -> None:
        self.app = Sanic(name="FamiliaAPI")
        self.model_name = model_name
        self.model_path = model_path
        self.n_workers = n_workers if n_workers else 1
        self.model_dir = model_path
        self.emb_file = model_path + "/word2vec.model"
        self.inference_engine_lda = InferenceEngineWrapper(self.model_dir, self.emb_file)
        self.inference_engine_slda = InferenceEngineWrapper(self.model_dir, self.emb_file, model_type="SLDA")
        self.twe = TopicalWordEmbeddingsWrapper(self.emb_file)
        self.lda_topic_words = self._read_topic_words_from_file()
        self._setup_routes()

    def _read_topic_words_from_file(self, topic_words_file_name: str = 'topic_words.lda.txt') -> defaultdict:
        topic_words = defaultdict(list)
        try:
            with open(self.model_path + "/" + topic_words_file_name) as f:
                for line in f:
                    parts = line.strip().split()
                    topic_id = int(parts[0])
                    word = parts[1]
                    score = float(parts[2])
                    topic_words[topic_id].append((word, score))
        except FileNotFoundError:
            logger.warning(f"Topic words file not found: {topic_words_file_name}")
        return topic_words

    def _get_param(self, request: Request, param_name: str, default_value=None, is_list: bool = False) -> Union[str, List[str]]:
        if is_list:
            param = request.args.getlist(param_name, default_value)
            if param == default_value:
                return param
            return [self._strip_to_none(p) for p in param]
        else:
            param = request.args.get(param_name, default_value)
            return self._strip_to_none(param)

    def _strip_to_none(self, text: str) -> Optional[str]:
        if text is not None:
            text = text.strip()
            if text == "":
                return None
        return text

    def _response(self, success: bool = True, data=None, message=None) -> HTTPResponse:
        return res.json({"success": success, "data": data, "message": message})

    def _error_response(self, message: str = 'Invalid request') -> HTTPResponse:
        return self._response(success=False, message=message)

    def _setup_routes(self) -> None:
        self.app.add_route(self.tokenize, '/tokenize', methods=['GET', 'POST'])
        self.app.add_route(self.infer_topics, '/infer', methods=['GET', 'POST'])

    async def tokenize(self, request: Request) -> HTTPResponse:
        text = self._get_param(request, 'text')
        if not text:
            return self._error_response("Text parameter is required.")
        tokens = self.inference_engine_lda.tokenize(text)
        return self._response(data={"tokens": tokens})

    async def infer_topics(self, request: Request) -> HTTPResponse:
        text = self._get_param(request, 'text')
        if not text:
            return self._error_response("Text parameter is required.")
        topics = self.inference_engine_lda.infer(text)
        return self._response(data={"topics": topics})


    def run(self) -> None:
        self.app.run(workers=self.n_workers)

