        cls.to_server_queue.put(value)

    @classmethod
    def recv_from_server(cls, non_blocking=False):
        try:
            return cls.from_server_queue.get(block=not non_blocking)
        except Queue.Empty:
            return None

    @classmethod
    def open_url(cls, path="/"):
