class JSONEchoServer(threading.Thread):

    def __init__(self, lock, port, *args, **kw):
        super().__init__(*args, **kw)
        self.lock = lock
        self.port = port
        self._stop = threading.Event()
        self.daemon = True

    def stop(self):
        self._stop.set()

    def stopped(self):
        return self._stop.is_set()

    def setup_application(self):
        class MainHandler(tornado.web.RequestHandler):
            def get(self):
                response = {"method": "GET", "data": self.request.arguments}
                self.write(json.dumps(response))

            def post(self):
                try:
                    data = json.loads(self.request.body.decode('utf-8'))
                except json.JSONDecodeError:
                    data = self.request.body.decode('utf-8')
                response = {"method": "POST", "data": data}
                self.write(json.dumps(response))

        return tornado.web.Application([
            (r"/", MainHandler),
        ])

    def run(self):
        app = self.setup_application()
        server = HTTPServer(app)
        server.listen(self.port)

        with self.lock:  # Notify that the server is ready
            self.lock.notify_all()

        io_loop = tornado.ioloop.IOLoop.current()
        while not self.stopped():
            io_loop.start_on_error()
            io_loop.current().stop()

        server.stop()
        io_loop.close(all_fds=True)

