import re
from http.client import HTTPConnection, HTTPSConnection
from typing import Any, Callable, List, Optional, Tuple, Union

class JSONRPCExecutor:

    def __init__(self, command: Union[str, List[str]], url: str, jsonrpc_method: str,
                 jsonrpc_params: Optional[List[Any]] = None, status: str = "^2\d\d$",
                 result_validator: Callable[[Any], Tuple[bool, Optional[str]]] = None,
                 verify_tls: bool = True, method: str = "POST"):
        self.command = command
        self.url = url
        self.jsonrpc_method = jsonrpc_method
        self.jsonrpc_params = jsonrpc_params if jsonrpc_params is not None else []
        self.status = status
        self.result_validator = result_validator
        self.verify_tls = verify_tls
        self.method = method

    def _send_request(self, conn: Union[HTTPConnection, HTTPSConnection]) -> None:
        headers = {'Content-type': 'application/json'}
        payload = {
            "jsonrpc": "2.0",
            "method": self.jsonrpc_method,
            "params": self.jsonrpc_params,
            "id": 1
        }
        conn.request(self.method, self.url, json.dumps(payload), headers)

    def _validate_response(self, response) -> bool:
        if not re.match(self.status, str(response.status)):
            return False
        try:
            data = json.loads(response.read().decode())
            if "error" in data:
                return False
            if self.result_validator:
                valid, message = self.result_validator(data.get("result"))
                return valid
            return True
        except (ValueError, json.JSONDecodeError):
            return False

    def execute(self) -> bool:
        parsed_url = self.url.split("://", 1)
        scheme = parsed_url[0].lower()
        netloc = parsed_url[1]
        if scheme == "https":
            conn = HTTPSConnection(netloc, context=None if not self.verify_tls else None)  # Use default context if verify_tls is True
        elif scheme == "http":
            conn = HTTPConnection(netloc)
        else:
            raise ValueError(f"Unsupported URL scheme: {scheme}")

        try:
            self._send_request(conn)
            response = conn.getresponse()
            return self._validate_response(response)
        finally:
            conn.close()

