        key_serializer = self.config.get('key_serializer', Serializer())
        value_serializer = self.config.get('value_serializer', Serializer())
        serialized_key = key_serializer.serialize(key)
        serialized_value = value_serializer.serialize(value)
        return self._producer._estimate_size_in_bytes(serialized_key, serialized_value, headers)

    def _ensure_valid_record_size(self, size):
