    def _get_protocol(protocol):
        if protocol == "MQTTv311":
            return mqtt.MQTTv311
        elif protocol == "MQTTv31":
            return mqtt.MQTTv31
        elif protocol == "MQTTv5":
            return mqtt.MQTTv5
        else:
            return mqtt.MQTTv311  # Default to MQTTv311
