        with tempfile.NamedTemporaryFile(mode="w", delete=False) as f:
            for word in words:
                f.write(word + "\n")
            temp_file_name = f.name
        try:
            results = self.execute(self.fst_model, temp_file_name, is_file=True, nbest=self.nbest)
        finally:
            os.remove(temp_file_name)
        return results

