                q.put(data)
            except ValueError:
                break

    def _stream_output(self, comm, shell):
        process = subprocess.Popen(comm, stdout=subprocess.PIPE, stderr=subprocess.PIPE, shell=shell, bufsize=1)
        q_out = queue.Queue()
        q_err = queue.Queue()
        t_out = threading.Thread(target=self._read_output, args=(process.stdout, q_out))
        t_err = threading.Thread(target=self._read_output, args=(process.stderr, q_err))
        t_out.start()
        t_err.start()

        stdout = ""
        stderr = ""

        while t_out.is_alive() or t_err.is_alive() or not q_out.empty() or not q_err.empty():
            try:
                out = self._decode(q_out.get_nowait())
                stdout += out
                print(out, end="", flush=True)  # Stream stdout
            except queue.Empty:
                pass
            try:
                err = self._decode(q_err.get_nowait())
                stderr += err
                print(err, end="", flush=True, file=sys.stderr)  # Stream stderr
            except queue.Empty:
                pass

        t_out.join()
        t_err.join()
        returncode = process.wait()
        return stdout, stderr, returncode

    def _decode(self, value):
        try:
            return value.decode("utf-8")
        except AttributeError:
            return value

    def _run_command(self, comm, shell):
        try:
            process = subprocess.Popen(comm, stdout=subprocess.PIPE, stderr=subprocess.PIPE, shell=shell)
