            stdout, stderr = process.communicate()

            if process.returncode != 0:
                raise RuntimeError(f"Amavisd-agent exited with error: {stderr}")

            for line in stdout.splitlines():
                for matcher in self.matchers:
                    match = matcher.match(line.strip())
                    if match:
                        if len(match.groups()) == 2:
                            yield match.group(1), 'count', match.group(2)
                        elif len(match.groups()) == 3:
                            yield match.group(1), match.group(3), match.group(2)
                        break

        except FileNotFoundError:
            raise RuntimeError(f"Amavisd-agent executable not found: {self.config['amavisd_exe']}")
        except Exception as e:
            raise RuntimeError(f"Error collecting amavisd-agent statistics: {e}")

