        output, error = process.communicate(data.encode('utf-8'))
        if error:
            detected_encoding = chardet.detect(error)['encoding'] or 'utf-8'
            error = error.decode(detected_encoding, 'ignore')
            raise RuntimeError(f"Error during HTML to text conversion: {error}")

