        self.__conn = None
        self.__service_url = None
        self.__url = None
        self.__id_count = 0
        self.__auth_header = None

        if service_url:
            self.__service_url = service_url
            self.__url = urllib.parse.urlparse(service_url)
        if btc_conf_file:
            authpair = self._parse_btc_conf_file(btc_conf_file)
            auth = f"{authpair[0]}:{authpair[1]}".encode('utf-8')
            self.__auth_header = b"Basic " + base64.b64encode(auth)

        if connection:
            self.__conn = connection

        self.timeout = timeout


    def _parse_btc_conf_file(self, btc_conf_file):
        with open(btc_conf_file, 'r') as f:
            for line in f:
                if line.startswith('rpcuser='):
                    rpcuser = line.strip().split('=')[1]
                elif line.startswith('rpcpassword='):
                    rpcpassword = line.strip().split('=')[1]
            return rpcuser, rpcpassword

    def _call(self, service_name, *args):
        self.__id_count += 1

        postdata = json.dumps({'version': '1.1',
                              'method': service_name,
                              'params': args,
                              'id': self.__id_count})

        headers = {
            'Host': self.__url.hostname,
            'User-Agent': 'jsonrpc',
            'Content-type': 'application/json',
        }

        if self.__auth_header:
            headers['Authorization'] = self.__auth_header

        if not self.__conn:
            if self.__url.scheme == 'https':
                self.__conn = http.client.HTTPSConnection(self.__url.hostname, self.__url.port if self.__url.port else 443, timeout=self.timeout)
            else:
                self.__conn = http.client.HTTPConnection(self.__url.hostname, self.__url.port if self.__url.port else 80, timeout=self.timeout)


        self.__conn.request('POST', self.__url.path, postdata, headers)

        return self._get_response()


    def _get_response(self):
        http_response = self.__conn.getresponse()
        if http_response is None:
            raise Exception("JSON-RPC: no response")

        response_str = http_response.read().decode('utf-8')

        try:
            response = json.loads(response_str)
        except json.decoder.JSONDecodeError:
            raise Exception(f"JSON-RPC: can't decode: {response_str}")

        if 'error' in response and response['error'] is not None:
            raise Exception("JSON-RPC: error: " + str(response['error']))

        return response['result']

    def close(self):
        if self.__conn:
            self.__conn.close()
            self.__conn = None

    def __del__(self):
        self.close()
