import socket
import threading
import time
import common

class Client:
    def __init__(self, host: str = common.DEFAULT_HOST, port: int = common.DEFAULT_PORT) -> None:
        self.host = host
        self.port = port
        self.pending_commands = []
        self.socket = None
        self.client_id = None
        self.current_custom_attributes = {}
        self.clients_attributes = {}
        self.rooms_attributes = {}
        self.current_room = None
        self.lock = threading.Lock()

    def __del__(self):
        self.disconnect()

    def connect(self) -> None:
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.socket.connect((self.host, self.port))
        self.send_command(common.Command(common.CommandType.GET_INITIAL_DATA))

    def disconnect(self) -> None:
        if self.is_connected():
            try:
                self.send_command(common.Command(common.CommandType.DISCONNECT))
                self.socket.shutdown(socket.SHUT_RDWR)
                self.socket.close()
            except OSError:
                pass
            finally:
                self.socket = None

    def is_connected(self) -> bool:
        return self.socket is not None

    def send_command(self, command: common.Command) -> bool:
        if self.is_connected():
            try:
                data = common.encode_command(command)
                with self.lock:
                    self.socket.sendall(data)
                return True
            except OSError:
                self.handle_connection_lost()
        return False


