    def fetch_outgoing_commands(self, commands_send_interval: int = 0) -> None:
        with self.lock:
            commands_to_send = self.pending_commands[:]
            self.pending_commands.clear()

        for command in commands_to_send:
            self.send_command(command)
            time.sleep(commands_send_interval)

    def fetch_commands(self, commands_send_interval: int = 0) -> list[common.Command]:
        self.fetch_outgoing_commands(commands_send_interval)
        return self.fetch_incoming_commands()

