        self.__csock.connect(sock)
        self.__deftout = timeout
        if timeout != -1:
            self.__csock.settimeout(timeout)

    def __del__(self):
        try:
            self.close()
        except Exception:
            pass

    def send(self, msg, nonblocking=False, timeout=None):
        if timeout is None:
            timeout = self.__deftout
        if timeout != -1:
            self.__csock.settimeout(timeout)

        msg = {"type": CSPROTO["REQUEST"], "payload": self.convert(msg)}
        self.__csock.sendall(pickle.dumps(msg))

        if nonblocking:
            return None

        return CSocket.receive(self.__csock, timeout=timeout)


    def settimeout(self, timeout):
        self.__deftout = timeout

    def close(self):
        try:
            msg = {"type": CSPROTO["REQUEST"], "payload": None}
            self.__csock.sendall(pickle.dumps(msg))
            self.__csock.shutdown(socket.SHUT_RDWR)
        except Exception:
            pass
        finally:
            self.__csock.close()


    @staticmethod
    def convert(m):
        if isinstance(m, set):
            return list(m)
        return m

    @staticmethod
    def receive(sock, nonblocking=False, timeout=None):
        if nonblocking:
            ready = select.select([sock], [], [], 0.0)
            if ready[0]:
                data = sock.recv(4096)
            else:
                return None
        else:
            if timeout:
                sock.settimeout(timeout)
            data = sock.recv(4096)

        if not data:
            return None

        try:
            msg = pickle.loads(data)
            if msg["type"] == CSPROTO["REPLY"]:
                return msg["payload"]
            elif msg["type"] == CSPROTO["EXCEPTION"]:
                raise Exception(msg["payload"])
            elif msg["type"] == CSPROTO["TIMEOUT"]:
                raise TimeoutError(msg["payload"])
        except (EOFError, pickle.UnpicklingError):
            return None
        return None

