import socket
import struct
from typing import Union, List, Dict
from cmpp_protocol import *

class CMPPClient:
    def __init__(self, host: str, port: int, sp_id: str, sp_secret: str, src_id: str, service_id: str) -> None:
        self.ip = host
        self.port = port
        self.sp_id = sp_id
        self.sp_secret = sp_secret
        self.src_id = src_id
        self.service_id = service_id
        self._sequence_id = 0
        self._is_connect = False
        self._times = 0
        self.__socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)

    @property
    def sequence_id(self) -> int:
        self._sequence_id += 1
        return self._sequence_id

    def _connect(self) -> None:
        while self._times < 3 and not self._is_connect:
            try:
                self.__socket.connect((self.ip, self.port))
                self._is_connect = True
            except OSError as e:
                self._times += 1
                if self._times == 3:
                    raise e

    def send(self, instance: Union[CMPPBaseRequestInstance, bytes]) -> None:
        if isinstance(instance, CMPPBaseRequestInstance):
            self.__socket.send(instance.to_bytes())
        elif isinstance(instance, bytes):
            self.__socket.send(instance)

    def recv(self) -> tuple:
        header = self.__socket.recv(12)
        if not header:
            return None, None
        total_length, command_id, sequence_id = struct.unpack(">III", header)
        body = self.__socket.recv(total_length - 12)
        return command_id, body

    def close(self) -> None:
        if self._is_connect:
            cmpp_terminate = CMPPTerminateRequest(self.sequence_id)
            self.send(cmpp_terminate)
            self.__socket.close()
            self._is_connect = False

    def _cmpp_connect(self) -> None:
        cmpp_connect = CMPPConnectRequest(self.sequence_id, self.sp_id, self.sp_secret)
        self.send(cmpp_connect)
        command_id, body = self.recv()
        if command_id == CMPP_CONNECT_RESP:
            cmpp_connect_resp = CMPPConnectResponse.from_bytes(body)
            if cmpp_connect_resp.status != 0:
                raise Exception(f"CMPP connect failed: {cmpp_connect_resp.status}")

    def _cmpp_send_sms(self, dest: List[str], sign_name: str, template_code: str, template_param: Dict) -> None:
        cmpp_submit = CMPPSubmitRequest(self.sequence_id, self.sp_id, self.service_id, self.src_id, dest, sign_name, template_code, template_param)
        self.send(cmpp_submit)
        command_id, body = self.recv()
        if command_id == CMPP_SUBMIT_RESP:
            cmpp_submit_resp = CMPPSubmitResponse.from_bytes(body)
            if cmpp_submit_resp.result != 0:
                raise Exception(f"CMPP submit failed: {cmpp_submit_resp.result}")

    def send_sms(self, dest: List[str], sign_name: str, template_code: str, template_param: Dict) -> None:
        try:
            self._connect()
            self._cmpp_connect()
            self._cmpp_send_sms(dest, sign_name, template_code, template_param)
        except Exception as e:
            print(f"Failed to send SMS: {e}")
        finally:
            self.close()

