                self.fh.write(buf[i:min(i + 1024, len(buf))])
        else:
            self.fh.write(buf)

    def _send_ack(self):
        if self.send_64bits_ack:
            self.dccsock.send((self.received & 0xffffffffffffffff).to_bytes(8, 'big'))
        else:
            self.dccsock.send((self.received & 0xffffffff).to_bytes(4, 'big'))

