                self.received += len(buf)
                self._write_func(buf)
                self.last_recv_size = len(buf)
                self._send_ack()
                self.update_progress()
            except socket.timeout:
                pass
        self.close()
        if self.abort:
            return "Aborted"
        return "Finished"

