import struct

class RConConnection:
    def __init__(self, target: str, port: int) -> None:
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.connect((target, port))
        self.rid = 0

    def send(self, t: int, payload: str) -> bytes:
        self.rid += 1
        packet = struct.pack("<iii", 10 + len(payload), self.rid, t) + payload.encode() + b'\x00\x00'
        self.sock.sendall(packet)
        response = b''
        while True:
            chunk = self.sock.recv(4096)
            if not chunk:
                break
            response += chunk
            if len(chunk) < 4096:
                break

        size, rid, t = struct.unpack("<iii", response[:12])
        if rid != self.rid:
            raise Exception("Invalid response ID")

        payload = response[12:size - 2].decode()
        return payload.encode()


    def login(self, password: str) -> None:
        response = self.send(3, password)
        if struct.unpack("<i", response[:4])[0] != self.rid:
            raise Exception("Authentication failed")


    def command(self, com: str, args: str) -> None:
        response = self.send(2, com + " " + args)
        return response.decode().rstrip('\x00')

    def close(self) -> None:
        self.sock.close()

