        super().__init__()
        self.ip = ip
        self.port = port
        self.listener_q = listener_q
        self.remote_q = remote_q
        self.config = config
        self.logger = log
        self.sock = None
        self.running = True

    def connect(self):
        try:
            self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            self.sock.connect((self.ip, self.port))
            self.sock.setblocking(False)
            return self.sock.getsockname()[1]
        except Exception as e:
            self.logger.error(f"Failed to connect: {e}")
            return None

    def run(self):
        while self.running:
            try:
                readable, writable, exceptional = select.select([self.sock], [self.sock], [self.sock], 1)
                if readable:
                    data = self.sock.recv(4096)
                    if not data:
                        self.running = False
                        break
                    self.listener_q.put(data)
                if writable:
                    try:
                        data = self.remote_q.get_nowait()
                        self.sock.sendall(data)
                    except queue.Empty:
                        pass
                if exceptional:
                    self.running = False
                    break
            except Exception as e:
                self.logger.error(f"Socket error: {e}")
                self.running = False
        if self.sock:
            self.sock.close()
            self.logger.info("Socket closed")

