            response.write(chunk)
        response.seek(0)
        return response

    def _dgram_send_and_receive(
        self, _socket: socket.socket, message: str, buffer_size=4096, *args
    ) -> BytesIO:
        _socket.sendto(message.encode(), self.target_address)
        response, _ = _socket.recvfrom(buffer_size)
        return BytesIO(response)

    def request(self, message: str, timeout=False, *args, **kwargs) -> BytesIO:
        if self.pool_size:
