import struct
import time
import sys

DEBUG = False
COOKIE_SIZE = 20
PARAM_EXCHANGE = 1
CREATE_STREAMS = 2
TEST_START = 3
TEST_RUNNING = 4
TEST_END = 5
EXCHANGE_RESULTS = 6
DISPLAY_RESULTS = 7
IPERF_DONE = 8

def server_once():
    l = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
    l.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
    l.bind(('', 5001))
    l.listen(5)

    while True:
        while True:
            rs, _, _ = select.select([l], [], [], 1)
            if rs:
                s, addr = l.accept()
                break

        if DEBUG:
            print('Client connected from %s:%s' % addr)

        cookie = s.recv(COOKIE_SIZE)
        if DEBUG:
            print('Cookie: %s' % cookie.decode())

        while True:
            command = struct.unpack('!i', s.recv(4))[0]

            if command == PARAM_EXCHANGE:
                params = s.recv(1024)
                if DEBUG:
                    print('Params received')

            elif command == CREATE_STREAMS:
                if DEBUG:
                    print('Streams created')

            elif command == TEST_START:
                if DEBUG:
                    print('Test started')
                start_time = time.time()

            elif command == TEST_RUNNING:
                if DEBUG:
                    print('Test running')

            elif command == TEST_END:
                if DEBUG:
                    print('Test ended')
                end_time = time.time()

            elif command == EXCHANGE_RESULTS:
                results = s.recv(1024)
                if DEBUG:
                    print('Results received')

            elif command == DISPLAY_RESULTS:
                print('Test duration: %.2f seconds' % (end_time - start_time))
                if DEBUG:
                    print('Results displayed')

            elif command == IPERF_DONE:
                if DEBUG:
                    print('IPerf done')
                break

        s.close()
        break

    l.close()

