        async with httpx.AsyncClient(headers=self._CLIENT_HEADER) as client:
            response = await client.post(self._uri, json={"question": question})
            if response.status_code == 200:
                return response.json().get("answer")
            return None

    def put(self, question: str, answer: str) -> int:
        return asyncio.run(self._put(question, answer))

    def get(self, question: str) -> str:
        return asyncio.run(self._get(question))

