        self.data = data


class RPCEndpoint:
    def __init__(self, method: str) -> None:
        self.method = method


class JSONBaseProvider:
    pass


class LegacyWebSocketProvider(JSONBaseProvider):
    _loop: ClassVar[Any] = asyncio.get_event_loop()
    endpoint_uri: Optional[Union[str, URI]] = None
    websocket_timeout: int = 30
    conn: PersistentWebSocket

    def __init__(self, endpoint_uri: str, websocket_timeout: int = 30, **websocket_kwargs: Any) -> None:
        if endpoint_uri is None:
            raise ValueError("endpoint_uri must be set")

        self.endpoint_uri = endpoint_uri
        self.websocket_timeout = websocket_timeout
        self.conn = PersistentWebSocket(endpoint_uri, **websocket_kwargs)

