        async with self.conn as ws:
            try:
                await ws.send(request_data)
                response = await asyncio.wait_for(ws.recv(), timeout=self.websocket_timeout)
                return RPCResponse(json.loads(response))
            except (ConnectionClosedError, ConnectionClosedOK, asyncio.TimeoutError) as e:
                raise e

    def make_request(self, method: RPCEndpoint, params: Any) -> RPCResponse:
        request_data = json.dumps({"method": method.method, "params": params, "jsonrpc": "2.0", "id": 1}).encode()
        return self._loop.run_until_complete(self.coro_make_request(request_data))

    def make_batch_request(self, requests: List[Tuple[RPCEndpoint, Any]]) -> List[RPCResponse]:
