    def message_callback(self, ws, message):
        message = json.loads(message)

        if "id" in message and message["id"] in self.commands:
            callback = self.commands.pop(message["id"])
            self.to_main_thread(callback, (message,))
        elif "notification" in message and message["notification"] in self.notifications:
            callback = self.notifications[message["notification"]]
            self.to_main_thread(callback, (message,))

    def open_callback(self, ws):
        if self.on_open:
            self.to_main_thread(self.on_open, ())

    def close_callback(self, ws):
        if self.on_close:
            self.to_main_thread(self.on_close, ())

    def to_main_thread(self, f, args):
        root = tkinter.Tk()
        root.withdraw()  # Hide the main window
        root.after_idle(f, *args)
        while root.dooneevent(tkinter._tkinter.DONT_WAIT):
            pass # Process events until the callback is executed


