        self._socket_sub = self._context.socket(zmq.SUB)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE, 1)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE_IDLE, 60)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE_INTVL, 10)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE_CNT, 3)

        self._active = False
        self._thread = None
        self._lock = threading.Lock()
        self._last_received_ping = time.time()

    def start(self, sub_address: str):
        with self._lock:
            if self._active:
                return

            self._socket_sub.connect(sub_address)
            self._active = True
            self._thread = threading.Thread(target=self.run, daemon=True)
            self._thread.start()

    def stop(self):
        with self._lock:
            self._active = False

    def join(self):
        if self._thread:
            self._thread.join()

    def run(self):
        while self._active:
            try:
                topic, data = self._socket_sub.recv_multipart(flags=zmq.NOBLOCK)
                topic = topic.decode()

                if topic == "ping":
                    with self._lock:
                        self._last_received_ping = time.time()
                else:
                    self.callback(topic, data)

            except zmq.Again:
                with self._lock:
                    if time.time() - self._last_received_ping > 60:  # Check for heartbeat timeout (60 seconds)
                        self.on_disconnected()
                        self._active = False
                time.sleep(0.1)
            except Exception as e:
                print(f"Error in message loop: {e}")
                self._active = False

