from typing import Any, Generator

class ZmqManager:
    def __init__(self, url: str = 'zmq+tcp://localhost:5555+5556', channel: str = 'socketio', write_only: bool = False) -> None:
        self.ctx = zmq.Context()
        url_parts = url.split('+')
        self.sink = self.ctx.socket(zmq.PUSH)
        self.sink.connect(url_parts[0])
        if not write_only:
            self.sub = self.ctx.socket(zmq.SUB)
            self.sub.connect(url_parts[1] if len(url_parts) > 1 else url_parts[0])
            self.channel = channel
            self.sub.setsockopt_string(zmq.SUBSCRIBE, self.channel)
        else:
            self.sub = None

    def _publish(self, data: Any) -> None:
        self.sink.send(pickle.dumps(data))

    def zmq_listen(self) -> Generator[bytes, None, None]:
        while True:
            yield self.sub.recv()

    def _listen(self) -> Generator[Any, None, None]:
        for raw_msg in self.zmq_listen():
            try:
                channel, data = pickle.loads(raw_msg)
                if channel == self.channel:
                    yield data
            except (pickle.UnpicklingError, ValueError):
                pass

