import zmq
import numpy as np
import uuid
import time
from typing import List, Union, Optional, Generator, NamedTuple

Response = NamedTuple('Response', [('id', int), ('array', np.ndarray)])

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.receiver = self.context.socket(zmq.SUB)
        self.identity = identity or str(uuid.uuid4()).encode('ascii')
        self.request_id = 0
        self.timeout = timeout
        self.pending_request = set()

        self.sender.connect(f"tcp://{ip}:{port}")
        self.receiver.connect(f"tcp://{ip}:{port_out}")
        self.receiver.setsockopt(zmq.SUBSCRIBE, self.identity)

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        self.sender.send_multipart([self.identity, b'%d' % self.request_id, msg])
        self.pending_request.add(self.request_id)
        self.request_id += 1

    def _recv(self):
        try:
            if self.timeout > 0:
                self.receiver.RCVTIMEO = self.timeout * 1000
            identity, req_id, msg = self.receiver.recv_multipart()
            req_id = int(req_id)
            if identity != self.identity:
                raise ValueError(f"Unexpected identity: {identity}, expected {self.identity}")
            return req_id, msg
        except zmq.error.Again:
            return None, None

    def _recv_ndarray(self):
        req_id, msg = self._recv()
        if msg is not None:
            arr = np.frombuffer(msg, dtype=np.float32)
            return Response(req_id, arr)
        return None

    def encode(self, texts, blocking=True):
        if isinstance(texts, str):
            texts = [texts]
        texts = [t.encode('utf-8') for t in texts]
        self._send(b'\0'.join(texts), len(texts))
        if blocking:
            r = self.fetch_all(concat=True)
            return r[0] if len(r) == 1 else r

    def fetch(self, delay=.0):
        while self.pending_request:
            response = self._recv_ndarray()
            if response is None:
                time.sleep(delay)
                continue
            self.pending_request.remove(response.id)
            yield response

    def fetch_all(self, sort=True, concat=False):
        responses = list(self.fetch())
        if sort:
            responses.sort(key=lambda x: x.id)
        if concat:
            if responses:
                return [np.concatenate([resp.array for resp in responses])]
            else:
                return []
        return [resp.array for resp in responses]

