import zmq
import numpy as np
from threading import Thread
from collections import deque
import logging

class NetGear:
    def __init__(self, address=None, port=None, protocol=None, pattern=0, receive_mode=False, logging=False, **options):
        self.__logging = logging
        if self.__logging:
            logging.basicConfig(level=logging.DEBUG)

        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False

        if protocol is None:
            protocol = "tcp"

        if address is None:
            address = "127.0.0.1"

        if port is None:
            port = "5555"

        if self.__pattern == 2:
            if self.__receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.SUB)
                self.__msg_socket.setsockopt_string(zmq.SUBSCRIBE, "")
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.PUB)
        elif self.__pattern == 1:
            if self.__receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.REP)
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.REQ)
        else:
            self.__msg_socket = self.__msg_context.socket(zmq.PAIR)

        connection_string = f"{protocol}://{address}:{port}"
        if self.__receive_mode:
            self.__msg_socket.bind(connection_string)
        else:
            self.__msg_socket.connect(connection_string)

        self.__subscriber_timeout = options.get("subscriber_timeout", 1000)
        self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout)

        if self.__receive_mode:
            self.__queue = deque()
            self.__thread = Thread(target=self.__recv_handler, daemon=True)
            self.__thread.start()

    def __recv_handler(self):
        while not self.__terminate:
            try:
                frame = self.__msg_socket.recv()
                self.__queue.append(frame)
            except zmq.error.Again:
                if self.__logging:
                    logging.debug("No message received within timeout")
                continue

    def recv(self):
        if self.__receive_mode:
            if self.__queue:
                return self.__queue.popleft()
            else:
                return None
        else:
            raise Exception("NetGear instance is not in receive mode")

    def send(self, frame, message=None):
        if not self.__receive_mode:
            if message is not None:
                self.__msg_socket.send_multipart([message.encode(), frame])
            else:
                self.__msg_socket.send(frame)
        else:
            raise Exception("NetGear instance is not in send mode")

    def close(self, kill=False):
        self.__terminate = True
        if self.__receive_mode:
            if kill:
                self.__thread.join(0)
            else:
                self.__thread.join()
        self.__msg_socket.close()
        self.__msg_context.term()

