import httpx
import hmac
import hashlib
import json
from typing import Dict, Any, Optional

class ExternalActionResponse:
    def __init__(self, status_code: int, data: Optional[Dict[str, Any]] = None, error: Optional[str] = None):
        self.status_code = status_code
        self.data = data
        self.error = error

class ExternalActionsRequester:
    def __init__(self, url: str) -> None:
        self.url = url

    async def send_request(self, payload: Dict[str, Any], signature_secret: str, additional_payload_values: Dict[str, Any] = {}, additional_headers: Dict[str, str] = {}, transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2)) -> ExternalActionResponse:
        payload.update(additional_payload_values)
        payload_bytes = json.dumps(payload, separators=(',', ':')).encode('utf-8')
        signature = self._encode_payload(payload_bytes, signature_secret)

        headers = {
            'Content-Type': 'application/json',
            'X-Signature': signature
        }
        headers.update(additional_headers)

        try:
            async with httpx.AsyncClient(transport=transport) as client:
                response = await client.post(self.url, content=payload_bytes, headers=headers)
                response.raise_for_status()
                return self._validate_response(response.json())
        except httpx.HTTPError as e:
            return ExternalActionResponse(e.response.status_code if e.response else 500, error=str(e))
        except json.JSONDecodeError:
            return ExternalActionResponse(500, error="Invalid JSON response")
        except Exception as e:
            return ExternalActionResponse(500, error=str(e))


    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        signature = hmac.new(signature_secret.encode('utf-8'), payload, hashlib.sha256).hexdigest()
        return signature

    def _validate_response(self, response: Dict[str, Any]) -> ExternalActionResponse:
        if 'error' in response:
            return ExternalActionResponse(response.get('status_code', 500), error=response['error'])
        elif 'data' in response:
            return ExternalActionResponse(response.get('status_code', 200), data=response['data'])
        else:
            return ExternalActionResponse(500, error="Invalid response format")

