import os

def google_search(keywords, paper_num=1):
    api_key = os.environ.get("GOOGLE_CSE_API_KEY")
    cse_id = os.environ.get("GOOGLE_CSE_ID")
    if not api_key or not cse_id:
        return "Error: GOOGLE_CSE_API_KEY and GOOGLE_CSE_ID environment variables must be set."

    if isinstance(keywords, list):
        query = " ".join(keywords)
    else:
        query = keywords

    start_index = (paper_num - 1) * 10 + 1
    url = f"https://www.googleapis.com/customsearch/v1?key={api_key}&cx={cse_id}&q={query}&start={start_index}"

    try:
        response = requests.get(url)
        response.raise_for_status()

        print(f"Status code: {response.status_code}")
        print(f"Response body: {response.text}")

        data = response.json()

        if "items" in data:
            results = []
            for item in data["items"]:
                snippet = item.get("snippet", "")
                link = item.get("link", "")
                try:
                    date = item["pagemap"]["metatags"][0]["article:published_time"]
                except (KeyError, IndexError):
                    date = "N/A"
                results.append(f"Date: {date}\nSnippet: {snippet}\nURL: {link}\n---\n")
            return "".join(results)
        else:
            if "error" in data:
                return f"Error: {data['error']['message']}"
            else:
                return "No results found. Try checking the next page."

    except requests.exceptions.RequestException as e:
        return f"Error: {e}"
    except Exception as e:
        return f"An unexpected error occurred: {e}"

