import requests
import json
from typing import Tuple, Optional

class AmazonSSO:
    access_token: Optional[str] = None
    refresh_token: Optional[str] = None
    client_id: str
    client_secret: str
    user_pool_id: str
    region: str
    user_info: dict = {}

    def __init__(self, client_id: str, client_secret: str, user_pool_id: str, region: str, access_token: str = None, refresh_token: str = None) -> None:
        self.client_id = client_id
        self.client_secret = client_secret
        self.user_pool_id = user_pool_id
        self.region = region
        self.access_token = access_token
        self.refresh_token = refresh_token

    def get_new_token(self) -> Optional[str]:
        url = f"https://cognito-idp.{self.region}.amazonaws.com/{self.user_pool_id}/oauth2/token"
        data = {
            "grant_type": "refresh_token",
            "client_id": self.client_id,
            "client_secret": self.client_secret,
            "refresh_token": self.refresh_token
        }
        headers = {"Content-Type": "application/x-www-form-urlencoded"}
        response = requests.post(url, data=data, headers=headers)
        if response.status_code == 200:
            response_json = response.json()
            self.access_token = response_json.get("access_token")
            return self.access_token
        return None

    def get_user_info(self) -> dict:
        url = f"https://cognito-idp.{self.region}.amazonaws.com/{self.user_pool_id}/oauth2/userInfo"
        headers = {"Authorization": f"Bearer {self.access_token}"}
        response = requests.get(url, headers=headers)
        if response.status_code == 200:
            self.user_info = response.json()
            return self.user_info
        return {}

    @staticmethod
    def amazon_sso(client_id: str, client_secret: str, user_pool_id: str, region: str, code: str, redirect_uri: str = None) -> Tuple[Optional['AmazonSSO'], Optional[str]]:
        url = f"https://cognito-idp.{region}.amazonaws.com/{user_pool_id}/oauth2/token"
        data = {
            "grant_type": "authorization_code",
            "client_id": client_id,
            "client_secret": client_secret,
            "code": code,
            "redirect_uri": redirect_uri
        }
        headers = {"Content-Type": "application/x-www-form-urlencoded"}
        response = requests.post(url, data=data, headers=headers)

        if response.status_code == 200:
            response_json = response.json()
            access_token = response_json.get("access_token")
            refresh_token = response_json.get("refresh_token")
            sso_obj = AmazonSSO(client_id, client_secret, user_pool_id, region, access_token, refresh_token)
            return sso_obj, None
        else:
            error_message = f"Failed to exchange authorization code for tokens. Status code: {response.status_code}, Response: {response.text}"
            return None, error_message
