import os
import requests

def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    if not wav_path.endswith('.wav') or not txt_path.endswith('.txt'):
        print("Error: Files must have correct extensions (.wav and .txt).")
        return None

    wav_filename = os.path.basename(wav_path)
    txt_filename = os.path.basename(txt_path)

    if wav_filename.split('.')[0] != txt_filename.split('.')[0]:
        print("Error: Filenames must match.")
        return None

    with open(txt_path, 'r', encoding='utf-8') as file:
        transcript_content = file.read()

    files = {
        'audio': (wav_filename, open(wav_path, 'rb'), 'audio/wav'),
        'transcript': (txt_filename, transcript_content, 'text/plain')
    }

    try:
        response = requests.post(f'http://{server_addr}:{port}/transcriptions', files=files)
        response.raise_for_status()
        if debug:
            print(response.json())
        return response
    except requests.exceptions.RequestException as e:
        print(f"HTTP Request failed: {e}")
        return None

