        self.conn_kw = conn_kw
        self.headers = headers or {}
        self.maxsize = maxsize
        self.block = block
        self.pool = Queue(maxsize=maxsize)
        self.lock = Lock()

    def _new_conn(self):
        return HTTPConnection(host=self.host, port=self.port, timeout=self.timeout, **self.conn_kw)

    def _make_request(self, conn, method, url, timeout=_Default, chunked=False, **httplib_request_kw):
        try:
            conn.request(method, url, headers=self.headers, **httplib_request_kw)
            return conn.getresponse()
        except (http.client.HTTPException, TimeoutError) as e:
            raise MaxRetryError(self, url, e)

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        retries = retries if retries else self.retries
        timeout = timeout if timeout != _Default else self.timeout
        conn = None
        try:
            while True:
                try:
                    with self.lock:
                        conn = self.pool.get(block=self.block, timeout=pool_timeout)
                except Empty:
                    conn = self._new_conn()
                try:
                    response = self._make_request(conn, method, url, body=body, headers=headers, timeout=timeout, chunked=chunked, **response_kw)
                    if response.status < 400 or not retries:
                        break
                    retries = retries.increment(method, url, response=response)
                except MaxRetryError as e:
                    raise e
                finally:
                    if conn:
                        self.pool.put(conn)
                        conn = None
            return response
        finally:
            if conn and release_conn:
                self.pool.put(conn)

