        http_headers = self.generateRandomHeaders()
        return (request_url, http_headers)

    def stop(self):
        self.runnable = False
        self.closeConnections()
        self.terminate()

    def incCounter(self):
        try:
            self.counter[0] += 1
        except:
            pass

    def incFailed(self):
        try:
            self.counter[1] += 1
        except:
            pass

