            filepath = this_dir / "web" / self.path[1:]

            mimetype, _ = mimetypes.guess_type(str(filepath))
            assert mimetype is not None
            self.send_header("Content-type", mimetype)
            self.end_headers()

            with open(filepath, "rb") as fh:
                content = fh.read()
