    TOKEN = 'your_access_key_secret'
    PROXY = None
    TTL = None

class API:
    SITE = 'alidns.aliyuncs.com'
    METHOD = 'GET'

    def __init__(self):
        self.config = Config()
        self.connection = None

    def request(self, param=None, **params):
        if param is None:
            param = {}
        params.update(param)
        params['AccessKeyId'] = self.config.ID
        params['Format'] = 'JSON'
        params['SignatureMethod'] = 'HMAC-SHA1'
        params['SignatureNonce'] = str(datetime.datetime.utcnow().timestamp())
        params['SignatureVersion'] = '1.0'
        params['Timestamp'] = datetime.datetime.utcnow().strftime('%Y-%m-%dT%H:%M:%SZ')
        params['Version'] = '2015-01-09'
        signed_params = self.signature(params)
        query_string = urllib.parse.urlencode(signed_params)
        url = f'/{query_string}'
        self.connection = self._get_connection()
        self.connection.request(API.METHOD, url)
        response = self.getresponse()
        return self._parse_response(response)

    def signature(self, params):
        sorted_params = sorted(params.items(), key=lambda p: p[0])
        canonicalized_query_string = '&'.join(f'{urllib.parse.quote(k)}={urllib.parse.quote(v)}' for k, v in sorted_params)
        string_to_sign = f'{API.METHOD}&%2F&{urllib.parse.quote(canonicalized_query_string)}'
        h = hmac.new((self.config.TOKEN + '&').encode('utf-8'), string_to_sign.encode('utf-8'), hashlib.sha1)
        signature = base64.b64encode(h.digest()).decode('utf-8')
        params['Signature'] = signature
        return params

    def _get_connection(self):
        if self.config.PROXY:
            return http.client.HTTPSConnection(self.SITE, proxy_host=self.config.PROXY)
        else:
            return http.client.HTTPSConnection(self.SITE)

    def getresponse(self):
        return self.connection.getresponse()

    def close(self):
        if self.connection:
            self.connection.close()

    def _parse_response(self, response):
        if response.status == 200:
            return response.read().decode('utf-8')
        else:
            raise Exception(f'Error {response.status}: {response.reason}')

