        if isinstance(words, str):
            return self._translate_word(words)
        elif isinstance(words, list):
            return self._translate_words(words)
        else:
            self._logger.error("Input must be a string or a list of strings.")
            return {}

    def _parse_output(self, output):
        results = {}
        for line in output.splitlines():
            parts = line.split('\t')
            if len(parts) > 1:
                word, pronunciation = parts[0], parts[1]
                if word not in results:
                    results[word] = []
                results[word].append(pronunciation)
        return results

