class DNSPodAPI:
    def __init__(self, token_id=None, token_key=None, proxy=None, ttl=None):
        self.SITE = "dnsapi.cn"
        self.METHOD = "POST"
        self.TOKEN_PARAM = "login_token"
        self.DEFAULT = "默认"
        self.LENGTH = "length"

        self.ID = token_id
        self.TOKEN = token_key
        self.PROXY = proxy
