class TCPRelayHandler:
    def __init__(self, server, fd_to_handlers, loop, local_sock, config, dns_resolver, is_local):
        self._server = server
        self._fd_to_handlers = fd_to_handlers
        self._loop = loop
        self._local_sock = local_sock
        self._remote_sock = None
        self._config = config
        self._dns_resolver = dns_resolver
        self._is_local = is_local
        self._stage = STAGE_INIT
        self._create_encryptor(config)
        self._client_address = local_sock.getpeername()[:2]
        self._update_tcp_mss(local_sock)
        self._obfs = obfs.obfs(config['obfs'])
        self._protocol = obfs.obfs(config['protocol'])
        self._setup_server_info()
        local_sock.setblocking(False)
        self._local_sock_fd = local_sock.fileno()
        fd_to_handlers[self._local_sock_fd] = self
        loop.add(local_sock, eventloop.POLL_IN | eventloop.POLL_ERR, self._server)

    def _create_remote_socket(self, ip, port):
        addrs = socket.getaddrinfo(ip, port, 0, socket.SOCK_STREAM, socket.SOL_TCP)
        af, socktype, proto, canonname, sa = addrs[0]
        remote_sock = socket.socket(af, socktype, proto)
        remote_sock.setblocking(False)
        remote_sock.setsockopt(socket.SOL_TCP, socket.TCP_NODELAY, 1)
        self._remote_sock = remote_sock
        self._remote_sock_fd = remote_sock.fileno()
        self._fd_to_handlers[self._remote_sock_fd] = self
        return remote_sock

    def _handle_dns_resolved(self, result, error):
        if error:
            self.destroy()
            return
        if result:
            ip = result[1]
            if ip:
                try:
                    remote_sock = self._create_remote_socket(ip, self._remote_address[1])
                    remote_sock.connect((ip, self._remote_address[1]))
                    self._loop.add(remote_sock, eventloop.POLL_ERR | eventloop.POLL_OUT, self._server)
                    self._stage = STAGE_CONNECTING
                except Exception as e:
                    self.destroy()

    def _write_to_sock(self, data, sock):
        try:
            if sock == self._remote_sock:
                self._update_activity(len(data))
                sent = sock.send(data)
                if sent < len(data):
                    return data[sent:]
            return None
        except (OSError, IOError) as e:
            error_no = eventloop.errno_from_exception(e)
            if error_no not in (errno.EAGAIN, errno.EINPROGRESS, errno.EWOULDBLOCK):
                self.destroy()
            return data

    def _on_local_read(self):
        try:
            data = self._local_sock.recv(BUF_SIZE)
            if not data:
                self.destroy()
                return
            if self._is_local:
                if self._encryptor is not None:
                    data = self._protocol.client_pre_encrypt(data)
                    data = self._encryptor.encrypt(data)
                    data = self._obfs.client_encode(data)
            self._write_to_sock(data, self._remote_sock)
        except (OSError, IOError) as e:
            error_no = eventloop.errno_from_exception(e)
            if error_no not in (errno.EAGAIN, errno.EWOULDBLOCK):
                self.destroy()

    def _on_remote_read(self):
        try:
            data = self._remote_sock.recv(BUF_SIZE)
            if not data:
                self.destroy()
                return
            if self._is_local:
                obfs_decode = self._obfs.client_decode(data)
                data = self._encryptor.decrypt(obfs_decode[0])
                data = self._protocol.client_post_decrypt(data)
            self._write_to_sock(data, self._local_sock)
        except (OSError, IOError) as e:
            error_no = eventloop.errno_from_exception(e)
            if error_no not in (errno.EAGAIN, errno.EWOULDBLOCK):
                self.destroy()

    def _on_remote_write(self):
        self._stage = STAGE_STREAM
        if self._data_to_write_to_remote:
            data = b''.join(self._data_to_write_to_remote)
            self._data_to_write_to_remote = []
            remaining = self._write_to_sock(data, self._remote_sock)
            if remaining is not None:
                self._data_to_write_to_remote.append(remaining)

    def handle_event(self, sock, fd, event):
        if fd == self._remote_sock_fd:
            if event & eventloop.POLL_ERR:
                self.destroy()
            elif event & eventloop.POLL_IN:
                self._on_remote_read()
            elif event & eventloop.POLL_OUT:
                self._on_remote_write()
        elif fd == self._local_sock_fd:
            if event & eventloop.POLL_ERR:
                self.destroy()
            elif event & eventloop.POLL_IN:
                self._on_local_read()
        return True

    def destroy(self):
        if self._remote_sock:
            self._loop.removefd(self._remote_sock_fd)
            del self._fd_to_handlers[self._remote_sock_fd]
            self._remote_sock.close()
        if self._local_sock:
            self._loop.removefd(self._local_sock_fd)
            del self._fd_to_handlers[self._local_sock_fd]
            self._local_sock.close()
        self._server.remove_handler(self)

