import threading
import typing
import weakref
from typing import Any, Union

class SafeStreamSocket:
    def __init__(self, addr: Union[str, typing.Tuple[str, int], socket.socket, Any]):
        self._id = self._acquire_uid()
        self._dup_sock = None
        self._name = None
        self._sock = self._connect(addr)
        self._finalizer = weakref.finalize(self, self._cleanup)

    def _acquire_uid(self) -> int:
        with threading.Lock():
            self._id_numbers.append(self._n[0])
            _id = self._n[0]
            self._n[0] += 1
        logging.debug("Opening socket: id=%d", _id)
        return _id

    def _connect(self, addr: Union[str, typing.Tuple[str, int], socket.socket, Any]) -> socket.socket:
        if isinstance(addr, socket.socket):
            return addr
        if isinstance(addr, str):
            if ':' in addr:
                host, port = addr.split(":", 1)
                addr = (host, int(port))
                family = socket.AF_INET
            elif os.path.exists(addr):
                family = socket.AF_UNIX
            else:
                raise SocketError(f"socket unix:{addr} unable to connect")
        else:
            family = socket.AF_INET
        sock = socket.socket(family, socket.SOCK_STREAM)
        sock.connect(addr)
        return sock

    def _cleanup(self):
        self._release_uid(self.id)
        sock = self._dup_sock or self._sock
        try:
            sock.shutdown(socket.SHUT_RDWR)
        except OSError:
            pass
        sock.close()

    def _release_uid(self, id: int):
        try:
            self._id_numbers.remove(id)
        except ValueError:
            pass
        logging.debug("Closing socket, id=%d", id)

    def close(self):
        self._finalizer()

    @property
    def closed(self) -> bool:
        return not self._finalizer.alive

    @property
    def id(self) -> int:
        return self._id

    @property
    def name(self) -> str:
        return self._name

    @name.setter
    def name(self, new_name: str):
        self._name = new_name

    def get_socket(self) -> socket.socket:
        return self._sock

    def recv(self, bufsize: int = 4096) -> bytes:
        try:
            return self._sock.recv(bufsize)
        except socket.timeout as e:
            raise SocketError("socket timeout") from e
        except ssl.SSLError as e:
            raise SocketError("ssl error") from e
        except Exception as e:
            raise SocketError("socket error") from e

    def recvall(self, size: int) -> bytearray:
        buf = bytearray()
        while len(buf) < size:
            chunk = self.recv(size-len(buf))
            if not chunk:
                raise SocketError("recvall: socket connection broken")
            buf.extend(chunk)
        return buf

    def sendall(self, data: Union[bytes, bytearray]) -> int:
        try:
            return self._sock.sendall(data)
        except Exception as e:
            raise SocketError("sendall error") from e

    def switch_to_ssl(self, pemfile):
        context = ssl.SSLContext(ssl.PROTOCOL_TLS)
        try:
            context.verify_mode = ssl.CERT_NONE
            context.set_ciphers("ALL:@SECLEVEL=0")
        except ssl.SSLError:
            pass
        self._dup_sock = self._sock.dup()
        context.load_cert_chain(pemfile, keyfile=pemfile)
        context.check_hostname = False
        self._sock = context.wrap_socket(self._sock, server_hostname="iphone.localhost")

    def __enter__(self):
        return self

    def __exit__(self, *args):
        self.close()

