        for cmsg_level_received, cmsg_type_received, cmsg_data in ancdata:
            if cmsg_level_received == cmsg_level and cmsg_type_received == cmsg_type:
                if cmsg_level == socket.IPPROTO_IP:
                    orig_dst = socket.inet_ntop(cmsg_level, cmsg_data[:4]), struct.unpack('!H', cmsg_data[4:6])[0]
                else:
                    orig_dst = socket.inet_ntop(cmsg_level, cmsg_data[:16]), struct.unpack('!H', cmsg_data[16:18])[0]
                break
        return addr, orig_dst, msg

    def _recv_udp_socket_ext(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        msg, ancdata, flags, addr = listener.recvmsg(bufsize, socket.CMSG_SPACE(256))
        orig_dst = None
        for cmsg_level, cmsg_type, cmsg_data in ancdata:
            if cmsg_level == socket.IPPROTO_IP and cmsg_type == socket.IP_ORIGDSTADDR:
