            elif cmsg_level == socket.IPPROTO_IPV6 and cmsg_type == socket.IPV6_ORIGDSTADDR:
                orig_dst = socket.inet_ntop(cmsg_level, cmsg_data[:16]), struct.unpack('!H', cmsg_data[16:18])[0]
