    def _recv_udp_fallback(self, listener: socket.socket, bufsize: int) -> Tuple[Any, None, bytes]:
        msg, addr = listener.recvfrom(bufsize)
        return addr, None, msg

    def send_udp(self, sock: socket.socket, srcip: Optional[Tuple[str, int]], dstip: Tuple[str, int], data: bytes) -> None:
        if srcip:
            if ':' in srcip[0]:
                level = socket.IPPROTO_IPV6
                optname = socket.IPV6_PKTINFO
                value = socket.inet_pton(level, srcip[0]) + struct.pack('!I', 0)
            else:
                level = socket.IPPROTO_IP
                optname = socket.IP_PKTINFO
                value = socket.inet_pton(level, srcip[0])
            sock.setsockopt(level, optname, value)
        sock.sendto(data, dstip)

    def setup_udp_listener(self, udp_listener: socket.socket) -> None:
        udp_listener.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        if hasattr(socket, 'IP_TRANSPARENT'):
            udp_listener.setsockopt(socket.IPPROTO_IP, socket.IP_TRANSPARENT, 1)
        if hasattr(socket, 'IPV6_TRANSPARENT'):
            udp_listener.setsockopt(socket.IPPROTO_IPV6, socket.IPV6_TRANSPARENT, 1)
        udp_listener.bind(('::', udp_listener.getsockname()[1]))

