        subprotocols = headers.get('sec-websocket-protocol', '').split(',')
        for protocol in subprotocols:
            if protocol.strip() in [proto.value for proto in WebsocketSubProtocols]:
                return WebsocketSubProtocols(protocol.strip())
        return WebsocketSubProtocols.JSON

    async def connect(self, websocket: WebSocket) -> None:
        await websocket.accept()
        client_id = self.get_client(websocket)
        self.protocol_dict[client_id] = self.get_subprotocol(websocket.headers)
        self.active_connections.append(websocket)

    async def disconnect(self, websocket: WebSocket) -> None:
        client_id = self.get_client(websocket)
        self.active_connections.remove(websocket)
        if client_id in self.protocol_dict:
            del self.protocol_dict[client_id]

    async def receive(self, websocket: WebSocket) -> Any:
        client_id = self.get_client(websocket)
        protocol = self.protocol_dict.get(client_id, WebsocketSubProtocols.JSON)
        if protocol == WebsocketSubProtocols.JSON:
            return json.loads(await websocket.receive_text())
        elif protocol == WebsocketSubProtocols.BYTES:
            return await websocket.receive_bytes()

    async def iter(self, websocket: WebSocket) -> AsyncIterator[Any]:
        while True:
            try:
                yield await self.receive(websocket)
            except Exception as e:
                print(f"Error receiving message: {e}")
                break

    async def send(self, websocket: WebSocket, data: Union[DataRequest, StatusMessage]) -> None:
        client_id = self.get_client(websocket)
        protocol = self.protocol_dict.get(client_id, WebsocketSubProtocols.JSON)
        if isinstance(data, DataRequest):
            message = {"request_id": data.request_id, "data": data.data}
        elif isinstance(data, StatusMessage):
            message = {"status": data.status, "message": data.message}
        else:
            raise ValueError("Unsupported data type")

        if protocol == WebsocketSubProtocols.JSON:
            await websocket.send_text(json.dumps(message))
        elif protocol == WebsocketSubProtocols.BYTES:
            await websocket.send_bytes(json.dumps(message).encode('utf-8'))

