    def stop(self):
        self._stop_event.set()
        self.server.stop(0)

    def join(self):
        self.server.wait_for_termination()

    class _Servicer(gnes_pb2_grpc.FrontendServiceServicer):
        def __init__(self, args):
            self.args = args

        def Call(self, request, context):
            with self.ZmqContext(self.args) as zmq_client:
                return zmq_client.send_request(request)

        def Train(self, request, context):
            with self.ZmqContext(self.args) as zmq_client:
                return zmq_client.send_request(request)

        def Index(self, request, context):
            with self.ZmqContext(self.args) as zmq_client:
                return zmq_client.send_request(request)

        def Search(self, request, context):
            with self.ZmqContext(self.args) as zmq_client:
                return zmq_client.send_request(request)

        def StreamCall(self, request_iterator, context):
            with self.ZmqContext(self.args) as zmq_client:
                for request in request_iterator:
                    yield zmq_client.send_request(request)

        class ZmqContext:
            def __init__(self, args):
                self.args = args
                self.local = threading.local()

            def __enter__(self):
                self.local.context = zmq.Context()
                self.local.socket = self.local.context.socket(zmq.REQ)
                self.local.socket.connect(self.args.zmq_address)
                return self

            def send_request(self, request):
                self.local.socket.send_pyobj(request)
                return self.local.socket.recv_pyobj()

            def __exit__(self, exc_type, exc_value, exc_traceback):
                self.local.socket.close()
                self.local.context.destroy()

