import torch
import torchvision.transforms.functional as F
from PIL import Image
import io
import base64
import requests

def upload_tensor_as_image(image_tensor, api_key, service='smms'):
    if not isinstance(image_tensor, torch.Tensor):
        return None

    # Convert tensor to PIL Image
    image = F.to_pil_image(image_tensor)

    # Save image to bytes buffer
    buffer = io.BytesIO()
    image.save(buffer, format='PNG')
    image_bytes = buffer.getvalue()

    # Encode image bytes to base64
    encoded_image = base64.b64encode(image_bytes).decode('utf-8')

    # Prepare data for upload
    if service == 'smms':
        url = 'https://sm.ms/api/v2/upload'
        files = {'smfile': ('image.png', image_bytes, 'image/png')}
        headers = {'Authorization': api_key}
        response = requests.post(url, headers=headers, files=files)
        try:
            response_json = response.json()
            if response_json['code'] == 'success':
                return response_json['data']['url']
            else:
                return response_json['msg']
        except Exception as e:
            return str(e)
    elif service == 'imgbb':
        url = 'https://api.imgbb.com/1/upload'
        payload = {
            'key': api_key,
            'image': encoded_image
        }
        response = requests.post(url, data=payload)
        try:
            response_json = response.json()
            if response_json['success']:
                return response_json['data']['url']
            else:
                return response_json['status_txt']
        except Exception as e:
            return str(e)
    else:
        return "Unsupported service"

    return None
